import React, { useState, useEffect } from 'react';
import { TextField, Button, List, Container, Typography } from '@mui/material';
import { sendMessage, receiveMessages } from '../api/greenApi';
import Message from './Message';

const Chat = () => {
  const [newMessage, setNewMessage] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [messages, setMessages] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Проверяем, авторизован ли пользователь
  useEffect(() => {
    const idInstance = localStorage.getItem('idInstance');
    const apiToken = localStorage.getItem('apiToken');

    if (idInstance && apiToken) {
      setIsLoggedIn(true);
    } else {
      setIsLoggedIn(false);
      return;
    }

    // Функция для получения сообщений
    const fetchMessages = async () => {
      const data = await receiveMessages();
      if (data) {
        const message = data.body?.messageData?.textMessageData?.textMessage;
        if (message) {
          setMessages((prev) => [...prev, { text: message, isMy: false }]);
        }
      }
    };

    // Запускаем таймер для получения сообщений
    const interval = setInterval(fetchMessages, 5000);
    return () => clearInterval(interval);
  }, []);

  // Функция для отправки сообщения
  const handleSend = async () => {
    if (!newMessage || !phoneNumber) {
      alert('Введите номер телефона и сообщение!');
      return;
    }

    try {
      // Отправляем сообщение
      await sendMessage(phoneNumber, newMessage);
      // Добавляем сообщение в список
      setMessages((prev) => [...prev, { text: newMessage, isMy: true }]);
      // Очищаем поле ввода
      setNewMessage('');
    } catch (error) {
      console.error('Ошибка отправки:', error);
      alert('Не удалось отправить сообщение. Проверьте данные и попробуйте снова.');
    }
  };

  // Если пользователь не авторизован
  if (!isLoggedIn) {
    return (
      <Typography variant="h6" align="center">
        Войдите в систему, чтобы начать чат.
      </Typography>
    );
  }

  // Интерфейс чата
  return (
    <Container maxWidth="sm">
      <TextField
        label="Номер получателя"
        value={phoneNumber}
        onChange={(e) => setPhoneNumber(e.target.value)}
        fullWidth
        margin="normal"
        placeholder="79991234567"
      />
      <List>
        {messages.map((msg, index) => (
          <Message key={index} text={msg.text} isMy={msg.isMy} />
        ))}
      </List>
      <div>
        <TextField
          label="Сообщение"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          fullWidth
          margin="normal"
          placeholder="Введите сообщение"
        />
        <Button onClick={handleSend} variant="contained" color="primary" fullWidth>
          Отправить
        </Button>
      </div>
    </Container>
  );
};

export default Chat;
