import React from 'react';
import { ListItem, Typography, Paper } from '@mui/material';

const Message = ({ text, isMy }) => {
  return (
    <ListItem sx={{ justifyContent: isMy ? 'flex-end' : 'flex-start' }}>
      <Paper
        sx={{
          p: 2,
          bgcolor: isMy ? '#DCF8C6' : '#FFFFFF',
          borderRadius: '10px',
        }}
      >
        <Typography>{text}</Typography>
      </Paper>
    </ListItem>
  );
};

export default Message;
