import React, { useState, useEffect } from 'react';
import { TextField, Button, List, Container, Typography } from '@mui/material';
import { sendMessage, receiveMessages } from '../api/greenApi';
import Message from './Message';

const Chat = () => {
  const [newMessage, setNewMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const phoneNumber = localStorage.getItem('phoneNumber');

  useEffect(() => {
    const interval = setInterval(async () => {
      const data = await receiveMessages();
      if (data) {
        const message = data.body.messageData.textMessageData.textMessage;
        setMessages((prev) => [...prev, { text: message, isMy: false }]);
      }
    }, 5000); // Проверяем каждые 5 секунд
    return () => clearInterval(interval);
  }, []);

  const handleSend = async () => {
    if (!newMessage) {
      alert('Введите сообщение!');
      return;
    }

    try {
      await sendMessage(phoneNumber, newMessage);
      setMessages((prev) => [...prev, { text: newMessage, isMy: true }]);
      setNewMessage('');
    } catch (error) {
      console.error('Ошибка отправки:', error);
      alert('Не удалось отправить сообщение. Проверьте данные и попробуйте снова.');
    }
  };

  return (
    <Container maxWidth="sm">
      <Typography variant="h6" align="center">
        Чат с {phoneNumber}
      </Typography>
      <List>
        {messages.map((msg, index) => (
          <Message key={index} text={msg.text} isMy={msg.isMy} />
        ))}
      </List>
      <div>
        <TextField
          label="Сообщение"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          fullWidth
          margin="normal"
          placeholder="Введите сообщение"
        />
        <Button onClick={handleSend} variant="contained" color="primary" fullWidth>
          Отправить
        </Button>
      </div>
    </Container>
  );
};

console.log(localStorage.getItem('idInstance'));
console.log(localStorage.getItem('apiToken'));

export default Chat;
