import React, { useState, useEffect } from 'react';
import { TextField, Button, List, Container } from '@mui/material';
import { sendMessage, receiveMessages } from '../api/greenApi';
import Message from './Message';

const Chat = () => {
  const [newMessage, setNewMessage] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const interval = setInterval(async () => {
      const data = await receiveMessages();
      if (data) {
        const message = data.body.messageData.textMessageData.textMessage;
        setMessages((prev) => [...prev, { text: message, isMy: false }]);
      }
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const handleSend = async () => {
    if (!newMessage || !phoneNumber) return;
    await sendMessage(phoneNumber, newMessage);
    setMessages((prev) => [...prev, { text: newMessage, isMy: true }]);
    setNewMessage('');
  };

  return (
    <Container maxWidth="sm">
      <TextField
        label="Номер получателя"
        value={phoneNumber}
        onChange={(e) => setPhoneNumber(e.target.value)}
        fullWidth
        margin="normal"
      />
      <List>
        {messages.map((msg, index) => (
          <Message key={index} text={msg.text} isMy={msg.isMy} />
        ))}
      </List>
      <div>
        <TextField
          label="Сообщение"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          fullWidth
          margin="normal"
        />
        <Button onClick={handleSend} variant="contained" color="primary">
          Отправить
        </Button>
      </div>
    </Container>
  );
};

export default Chat;
